    if (this.progress < 1 && this.timer == null) {
      for (var i = 0; i < this.pathArray.length; i++) {
        this.pathArray[i].tl.resume();
      }
      this.timer = setTimeout(()=> {
        for (var i = 0; i < this.pathArray.length; i++) {
          this.pathArray[i].tl.pause();
        }
        this.progress += 1/6;
        this.timer = null;
       }, this.duration/6)
     }
